    casted_user.password = await hash(casted_user.password, saltRounds);
    console.log(casted_user);
    user = await new Promise((resolve, _) => {
        findUserWithEmail(casted_user.email, (user) => {
            // console.log(user);
            if (user == null) {
                resolve(null);
            } else {
                resolve(user);
            }
        });
    });
