export async function sendUserInvitation(
    receiverEmail: string,
    senderEmail: string
) {
    try {
        var senderUser: any;

        senderUser = await new Promise((resolve, _) => {
            findUserWithEmail(senderEmail, (user) => {
                // console.log(user);
                if (user == null) {
                    resolve(null);
                } else {
                    resolve(user);
                }
            });
        });

